mapper.insert:
insert into t_station (station_name, city, nearby
  )
values (#{stationName,jdbcType=VARCHAR}, #{city,jdbcType=VARCHAR}, #{nearby,jdbcType=VARCHAR}
  )

mapper.update:
update t_station
set city = #{city,jdbcType=VARCHAR},
  nearby = #{nearby,jdbcType=VARCHAR}
where station_name = #{stationName,jdbcType=VARCHAR}

mapper.select:
select station_name, city, nearby
from t_station
where station_name = #{stationName,jdbcType=VARCHAR}
